mapper.select:
SELECT *
FROM(
SELECT
ROWNUM R, TT.*
FROM TB_CUSTOMER TT
WHERE DELETE_YN = 'N'
AND ROWNUM <= (#{page}+1)*#{size}
)
WHERE R > #{page}*#{size}

SELECT *
FROM(
SELECT
ROWNUM R, TT.*
FROM TB_CUSTOMER TT
WHERE DELETE_YN = 'N'
AND EMAIL LIKE '%' || #{email} || '%'
AND ROWNUM <= (#{page}+1)*#{size}
)
WHERE R > #{page}*#{size}
