mapper.select:
SELECT ID,TITLE,DESCRIPTION, PUBLISHED, DELETE_YN, INSERT_TIME, UPDATE_TIME, DELETE_TIME
FROM TB_TUTORIAL
WHERE DELETE_YN='N' AND PUBLISHED=#{published}

SELECT ID,TITLE,DESCRIPTION, PUBLISHED, DELETE_YN, INSERT_TIME, UPDATE_TIME, DELETE_TIME
FROM TB_TUTORIAL
WHERE DELETE_YN='N' AND ID=#{id}

mapper.update:
UPDATE TB_TUTORIAL
SET TITLE = #{title}
  , DESCRIPTION = #{description}
  , PUBLISHED = DECODE(#{published},'true','Y','N')
  , UPDATE_TIME = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE ID=#{id}

UPDATE TB_TUTORIAL
SET DELETE_YN = 'Y', DELETE_TIME = TO_CHAR(SYSDATE, 'YYYY-MM-DD HH24:MI:SS')
WHERE ID=#{id}
